"use strict";

const { Pool } = require('pg');

const pool = new Pool({
    user: process.env.USER,
    host: process.env.HOST,
    database: process.env.DB,
    password: process.env.PASSWORD,
    port: process.env.DBPORT,
});

module.exports = {
    query: (text, params) => pool.query(text, params)
}

